using System.Collections; using UnityEngine; using UnityEngine.UI;  public class AcceptButtonScript : MonoBehaviour{      public Text NoInternetText;      private TermsAndServicesScript GetTermsAndServicesScript;      // Functions     void Start(){          GetTermsAndServicesScript = FindObjectOfType<TermsAndServicesScript>();         NoInternetText.gameObject.SetActive(false);     }      public void MailFinishedSending(){          StartCoroutine(Continuar());     }      // _________________________________________________________________________________________________________________________________________________________________________________________      IEnumerator Continuar(){          if (Application.internetReachability == NetworkReachability.NotReachable){              NoInternetText.gameObject.SetActive(true);             NoInternetText.text = "Necesitas estar conectado al internet para poder aceptar los terminos y condiciones.";         }                       if(Application.internetReachability == NetworkReachability.ReachableViaLocalAreaNetwork){              // cuando acepta las condiciones de uso, se genera este playerPrefs, con el cual despues voy a              //evitar que el menu de terminos y condicciones vuelva a aparecer, mientras el PlayerPrefs exista.              PlayerPrefs.SetString("Accepted", "Acepto");              // Aqui mando el mail de los ToS             GetTermsAndServicesScript.SendMailFunction();             yield return new WaitUntil(() => GetTermsAndServicesScript.mailSend == true);              // Esta funció apaga el canvas de ToS.             GetTermsAndServicesScript.CloseToFCanvas();         }     } }  
