using System; using UnityEngine;  public class BaseDateTimeScript : MonoBehaviour {      // Este script contiene la lógica básica para guardar y contar los días que han pasado desde la primera vez que se entró la escena que contenga este script.     // Lo uso como base para todos los demás scripts que necesiten contar los días.      private DateTime FirstDayOfPlay;     private TimeSpan EndedPeriod;      private void Start()     {         SaveDay();         OverwritePreviousData();     }      void SaveDay()     {         if (PlayerPrefs.HasKey(KeysGames.KeyExercisesTimer))         {              FirstDayOfPlay = Convert.ToDateTime(PlayerPrefs.GetString(KeysGames.KeyExercisesTimer));             print("este es el primer día de juego" + FirstDayOfPlay.ToString());              EndedPeriod = DateTime.Now - FirstDayOfPlay;              print("cuantos días van = " + EndedPeriod.Days.ToString());         }          else         {             PlayerPrefs.SetString(KeysGames.KeyExercisesTimer, DateTime.Now.ToString());             print("estoy salvando la fecha porque no hay = " + PlayerPrefs.GetString(KeysGames.KeyExercisesTimer));         }     }      void OverwritePreviousData()     {         if (EndedPeriod.Days >= 2)         {              print("Voy a cambiar los ejercicios ");             print("aquí debo de cambiar la lógica para poder cargar las escenas “B” o “A”, como sea necesario ");              PlayerPrefs.DeleteKey(KeysGames.KeyExercisesTimer);             print("Aquí se borra la fecha que se había guardado, los ejercicios ya se debieron de haber cambiado para este punto, y vuelo a llamar la función que me va a salvar este nuevo día");             SaveDay();          }     } } 