using UnityEngine;  public class PercentagesScript : MonoBehaviour {      // Public Variables      public GameObject[] spawnStarsPoints;     public GameObject[] StarsObjs;      public float Totalcount;     public float finalScore;     public float score;      // private Variables      private int substractPoints;     private int multiplier;     private float Count;     private int stars;         // Script References      //private HungerScript hungerScriptReference;     private RaycastDistractions distractions;     private CardboardRaycast cardboard;        // Functions      // ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________      // simplemente busco el Raycast que detecta si estas interactuando con la distracción para usar funciones de ahí.      void Start(){          distractions = FindObjectOfType<RaycastDistractions>();         cardboard = FindObjectOfType<CardboardRaycast>();      }      //* ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________       // ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________      // Aqui solo actualizo de manera constante cuantos puntos le vamos a restar al final al niño por estar       private void Update()
    {         if ( cardboard != null)
        {             substractPoints = cardboard.negativePoints;         }          if(distractions != null){             substractPoints = distractions.negativePoints;         }
    }      //* ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________       // ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________      // aqui saco la calificación final de cada juego, Hay 4 posibles resultados para el niño: 3 estrellas, 2 estrellas, 1 estrella o 0 estrellas.       public void Percentanges(){                  Count = (score / Totalcount) * 100;
        Count = Count - substractPoints - KeysForAnalytic.foodErrors;          if(Count > 90){                          stars = 3;              for (int i = 0; i < stars; i++){                  GameObject go = Instantiate(StarsObjs[i],spawnStarsPoints[i].transform.position, spawnStarsPoints[i].transform.rotation);                 go.transform.parent = spawnStarsPoints[i].transform;             }         }          else if(Count > 60){              stars = 2;                         for (int i = 0; i < stars; i++)             {                 GameObject go = Instantiate(StarsObjs[i], spawnStarsPoints[i].transform.position, spawnStarsPoints[i].transform.rotation);                 go.transform.parent = spawnStarsPoints[i].transform;             }         }          else if(Count > 30){              stars = 1;              for (int i = 0; i < stars; i++)             {                 GameObject go =  Instantiate(StarsObjs[i], spawnStarsPoints[i].transform.position, spawnStarsPoints[i].transform.rotation);                 go.transform.parent = spawnStarsPoints[i].transform;             }         }          else{             stars = 0;         }     }      //* ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________       // ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________      // esta función hace lo que su nombre dice      public void IncreaseScore()
    {         this.score++;     }      //* ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________       // ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________      // Aqui simplemente igualo el conteo final de todos los puntos, y lo paso a una variable publica que no he tocado para nada en todo el codigo, asi todas las funciones que necesiten saber el resultado final del juego,     // solo tiene que llamar esta función, y despues igualar su variable local que vayan a utilizar ellos, a este valor de finalScore.      public void FinalScore(){          finalScore = Count;     }      //* ________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________________ } 